<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml" <?php language_attributes(); ?>><head profile="http://gmpg.org/xfn/11"><meta http-equiv="Content-Type" content="<?php bloginfo('html_type'); ?>; charset=<?php bloginfo('charset'); ?>" /><?php $the_title = wp_title(' - ', false); if ($the_title != '') : ?>    <title><?php echo wp_title('',false); ?> | <?php bloginfo('name'); ?></title><?php else : ?>    <title><?php bloginfo('name'); ?><?php if ( $paged > 1 ) echo ( ' - page '.$paged ); ?></title><?php endif; ?>	<?php //comments_popup_script(); // off by default ?>	<link rel="stylesheet" type="text/css" media="all" href="<?php bloginfo( 'stylesheet_url' ); ?>" />	<link rel="pingback" href="<?php bloginfo( 'pingback_url' ); ?>" /><?php if ( is_singular() ) wp_enqueue_script( 'comment-reply' ); ?>	<?php wp_head(); ?></head><body <?php body_class(); ?>><div id="header">		<?php $options = get_option('Summ_theme_options'); ?>		<div class="siteName">			<?php if ($options['logo_src']!='') { ?>				<a class="logo_img" href="<?php echo home_url(); ?>" title="<?php bloginfo('name'); ?>"><img src="<?php echo $options['logo_src']; ?>" /></a>			<?php } else { ?>				<a href="<?php echo home_url(); ?>"><?php bloginfo('name'); ?></a>			<?php } ?>		</div>		<?php if ($options['desc']=='true') {}else {?>			<div class="description"><?php bloginfo('description'); ?></div>		<?php } ?>			<div class="rss">				<a href="<?php if($options['rss_url'] != '') { echo($options['rss_url']); } else { bloginfo('rss2_url'); } ?>" rel="alternate" type="application/rss+xml" target="_blank"   title="Subscribe Me"></a>		</div>		<div id="globalNavi">			<?php wp_nav_menu( array( 'show_home' => __('Home', 'Summ'),'container' => 'globalNavi', 'theme_location' => 'primary') ); ?>				<div class="searchbox"><?php get_search_form(); ?></div>	</div></div>