<?php get_header(); ?><?php // search reminderif (is_category()) :	echo '<div class="wp-caption aligncenter"><p class="wp-caption-text"><strong>' . __('Category', 'Summ') . ': '; wp_title('',true,'right'); echo ' </strong></p></div>';elseif (is_tag()) :	echo '<div class="wp-caption aligncenter"><p class="wp-caption-text"><strong>' . __('Tag', 'Summ') . ': '; wp_title('',true,'right'); echo ' </strong></p></div>';elseif (is_search()) :	printf('<div class="wp-caption aligncenter"><p class="wp-caption-text">' . __('Search results for &#8220;%s&#8221;', 'Summ') . '</p></div>', '<strong>' . esc_html( get_search_query() ) . '</strong>');elseif (is_date()) :	echo '<div class="wp-caption aligncenter"><p class="wp-caption-text"><strong>' . __('Archives', 'Summ') . ': '; wp_title('',true,'right'); echo ' </strong></p></div>';endif;?><div id="content">	<div id="main">	<div class="section entry" id="entry<?php the_ID(); ?>">		<?php if(have_posts()) : ?><?php while(have_posts()) : the_post(); ?>		<div <?php post_class(); ?>>		<div class="post">			<h2>			<a href="<?php the_permalink() ?>"><?php the_title(); ?></a>			</h2><?php if ( comments_open() ) : ?><span class="cmtinfo"><?php comments_popup_link(); ?></span><?php endif; ?>				<p class="info">					<span class="dateinfo"><a href="<?php the_permalink() ?>"><?php $dateformat = get_option('date_format');$timeformat = get_option('time_format');the_time("$dateformat \a\\t $timeformat"); ?></span></a><span class="catinfo"><?php echo __('Category', 'Summ') ?>:<?php the_category(' | ') ?> <?php edit_post_link(__(' Edit','Summ'), ' &#124; ', ''); ?></span>				</p>				<?php $options = get_option('Summ_theme_options'); ?>			<div class="indexentry"><?php if ( $options['excerpt_check']=='true' ) { the_excerpt(__('&raquo; Read more...','Summ')); } else {the_content(__( 'Read More...','Summ' ));} ?></div>			<?php wp_link_pages(array('before' => '<p><strong>'.__('Pages').':</strong> ', 'after' => '</p>', 'next_or_number' => 'number')); ?>		</div></div>			<?php endwhile; ?>		<div class="flip">			<div class="prevpost"><?php next_posts_link(__('Older Entries', 'Summ')) ?></div>			<div class="nextpost"><?php previous_posts_link(__('Newer Entries', 'Summ')) ?></div>		</div>	<?php else : ?>	 <h2><?php echo __('Not Found','Summ'); ?></h2>	 <p class="center"><?php echo __('Sorry, but you are looking for something that is not here.','Summ'); ?></p>	 <?php get_search_form(); ?>	 		<?php endif; ?>	</div><!-- section entry -->	</div><!-- main -->	<?php	get_sidebar(); ?>	<?php	get_footer(); ?>